export class AuthorApi{

    baseUrl = "https://localhost:7196"

    async getAuthors(){
        return await fetch(`${this.baseUrl}/api/author/getAll`)
    }

    async addAuthor(author){
        return await fetch(`${this.baseUrl}/api/author/add`, {
            method: "POST",
            headers:{"Content-Type" : "application/json"},
            body: JSON.stringify(author),
        }).then(() =>{
            console.log("added")
        })}

    async deleteAuthor(id){
        return await fetch(`${this.baseUrl}/api/author/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}

    async getAuthorById(id){
        return await fetch(`${this.baseUrl}/api/author/${id}`)
    }
}
