import {useEffect, useState} from "@types/react";
import {AuthorApi} from "../../api/AuthorApi";
import {AddAuthor} from "../author/AddAuthor";
import {BookApi} from "../../api/BookApi";

export function BookItem() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [books, setBooks] = useState([]);

    useEffect(() => {
        let bookService = new BookApi()
        bookService.getBooks()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setBooks(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return (
        <div className="container" >
            <div className="content contact-list">
                <div className="card card-default">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddAuthor></AddAuthor>
                    </div>

                    <div className="card-body px-3 px-md-5" >
                        <div className="row" >
                            {books.map(book => <div key={book.id} className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                                <div className="card card-default p-4">
                                    <div className="media-body">
                                        <h5 className="mt-0 mb-2 text-dark">{book.name}</h5>
                                        <ul className="list-unstyled text-smoke text-smoke">
                                        <li className="d-flex">
                                            <i className="mdi mdi-map mr-1"></i>
                                            <span>{book.description}</span>
                                        </li>
                                        <li className="d-flex">
                                            <i className="mdi mdi-map mr-1"></i>
                                            <span>{book.photo}</span>
                                        </li>
                                            <li className="d-flex">
                                                <i className="mdi mdi-map mr-1"></i>
                                                <span>{book.author.name}</span>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div> )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
