import {useParams} from "react-router-dom";
import {UserApi} from "../../api/UserApi";
import {useEffect, useRef, useState} from "react";
import {BookApi} from "../../api/BookApi";
import {AuthorItem} from "./AuthorItem";
import {Button} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

export const AuthorBooks = () => {

    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const effectRan = useRef(false)

    const {id} = useParams()

    useEffect(() => {
        let bookApi = new BookApi()
        if (effectRan.current === false) {
            effectRan.current = true
            bookApi.getBooksByAuthorId(id)
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setBooks(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }
    },)

    return (

        <div>
            {books.map(book => <div key={book.id}
                                    className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                <div className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                    <div className="card card-default p-4">
                        <h5 className="mt-0 mb-2 text-dark">{book.name}</h5>
                        <div>
                            <img src={`data:image/jpeg;base64,${book.photo}`} height={100} className="position-relative top-50 start-50" />
                        </div>
                        <li className="d-flex">

                            <i className="mdi mdi-map mr-1"></i>
                            <span>{book.description}</span>
                        </li>
                        <p/>
                        {/*<div className="position-relative">*/}
                        {/*    <Button onClick={handleShow} className="btn btn-danger">*/}
                        {/*        <FontAwesomeIcon icon="trash"> </FontAwesomeIcon>*/}
                        {/*    </Button>*/}
                        {/*</div>*/}
                        </div>
                        </div>
            </div>)}
        </div>

    )

}
