import {useEffect, useState} from "@types/react";
import {BookApi} from "../../api/BookApi";
import {AddAuthor} from "../author/AddAuthor";
import {AddBook} from "./AddBook";
import {AuthorItem} from "../author/AuthorItem";
import {BookItem} from "./BookItem";

export function Book() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [books, setBooks] = useState([]);

    useEffect(() => {
        let bookService = new BookApi()
        debugger
        bookService.getBooks()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setBooks(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return (
        <div className="container">
            <div className="content contact-list">
                <div className="card card-default">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddBook></AddBook>
                    </div>
                    <div className="card-body px-3 px-md-5">
                        <div className="row">
                            {books.map(book => <BookItem book={book} key={book.id}
                                                               className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                            </BookItem>)}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
