import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {AddAuthor} from "./AddAuthor";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/fontawesome-free-solid'
import {AuthorItem} from "./AuthorItem";

export function Author() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [authors, setAuthors] = useState([]);

    useEffect(() => {
        let authorService = new AuthorApi()
        authorService.getAuthors()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setAuthors(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    function deleteAuthor(id) {
        let authorService = new AuthorApi()
        authorService.deleteAuthor(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    return (
        <div className="container">
            <div className="content contact-list">
                <div className="card card-default">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddAuthor></AddAuthor>
                    </div>
                    <div>
                        {authors.map(author => <AuthorItem key={author.id}
                                                           className="col-lg-6 col-xl-4 col-md-6 col-sm-12">

                        </AuthorItem>)};
                    </div>
                </div>
            </div>
        </div>
    )
}
