import {useEffect, useState} from "react";
import {UserApi} from "../../api/UserApi";
import {Button, Modal} from "react-bootstrap";
import {BookApi} from "../../api/BookApi";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {CategoryApi} from "../../api/CategoryApi";

export const AddBook = () => {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [authors, setAuthors] = useState([]);
    const [categories, setCategories] = useState([]);

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [photo, setPhoto] = useState('');

    const [authorId, setAuthorId] = useState("");
    const [categoriesIds, setCategoriesIds] = useState([]);

    let bookService = new BookApi()

    useEffect(() => {
        let authorService = new UserApi()
        let categoryService = new CategoryApi()
        authorService.getAuthors()
            .then(function
                (response) {
                console.log(response);
                setAuthors(response.data)
            }).catch(function (error) {
            console.log(error);
        });
        categoryService.getCategories()
            .then(function
                (response) {
                console.log(response);
                setCategories(response.data)
            }).catch(function (error) {
            console.log(error);
        });
    }, [])

    const handleSubmit = event => {
        debugger
        event.preventDefault();
        const book = {name, description, photo, authorId, categoriesIds};
        bookService.addBookByAdmin(book)
        // eslint-disable-next-line no-restricted-globals
        location.reload();

    }
    const onChangeCategory = (e) => {
            if (!categoriesIds.includes(e)) {
                categoriesIds.push(e)
            }
    }
    return (
    <>
            <Button variant="warning" onClick={handleShow}>
                <FontAwesomeIcon icon="fa-book"></FontAwesomeIcon> Add Book
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add Book</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className={"form"} onSubmit={handleSubmit}>
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={description}
                               placeholder="Description"
                               onChange={(e) => setDescription(e.target.value)}
                        ></input>
                        <p/>
                        Book Cover:
                        <input className="form-control-file"
                               type="file"
                               value={''}
                               placeholder="photo"
                               onChange={(e) => setPhoto(e.target.files[0])}
                        ></input>
                        <p/>
                        <select className="form-select"
                                onChange={(e) => onChangeCategory(e?.target?.value)}
                                multiple>
                                {categories.map(category => <option value={category.id} key={category.id}>
                                {category.name}
                            </option>)}
                        </select>
                        <p/>
                        <select className="form-select"
                                onChange={(e) => setAuthorId(e.target.value)}
                                value={authorId}>
                            {authors.map(author => <option value={author.id} key={author.id}>
                                {author.name}
                            </option>)}
                        </select>
                        <p/>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
