import axios from "axios";
import useAxiosPrivate from "./useAxiosPrivate";

export const BookApi = () => {

    const protectedAxios = useAxiosPrivate();
    const controller = new AbortController();

    function getBooks(){
        protectedAxios.get('/api/book/getAll', {
            signal: controller.signal
        });
    }
    function addBookByAdmin(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return axios
            .post(`/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
            })
            .catch((err) => {
                console.log(err);
            });
    }

    function deleteBook(id){
        return this.protectedAxios.delete(`/api/book/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}

    function addBookByAuthor(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return axios
            .post(`${this.baseUrl}/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
            })
            .catch((err) => {
                console.log(err);
            });
    }

    function getBooksByAuthorId(id){
        return axios.get(`/api/book/author/${id}`)
    }

}
