export class CategoryApi{
    baseUrl = "https://localhost:7196"

    async getCategories(){
        return await fetch(`${this.baseUrl}/api/category/getAll`)
    }

    async addCategory(category){
        return await fetch(`${this.baseUrl}/api/category/add`, {
            method: "POST",
            headers:{"Content-Type" : "application/json"},
            body: JSON.stringify(category),
        }).then(() =>{
            console.log("added")
        })}

    async deleteCategory(id){
        return await fetch(`${this.baseUrl}/api/category/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}
}
