import axios, {Axios} from "axios";

export class BookApi{

    baseUrl = "https://localhost:7196"

    async getBooks(){
        return await fetch(`${this.baseUrl}/api/book/getAll`)
    }
    async addBook(book){
        debugger

        // return await fetch(`${this.baseUrl}/api/book/add`, {
        //     method: "POST",
        //     headers:{"Content-Type" : "application/json"},
        //     body: JSON.stringify(book)
        // }).then(() =>{
        //     console.log("added")
        // })
        Axios.post('https://localhost:7196/api/book/add', book,
            {
                "headers": {
                    "Content-Type" : "multipart/form-data"
                }
            })
            .then(
                res =>{
                    console.log(res.data)
                }
            )}

    async deleteBook(id){
        return await fetch(`${this.baseUrl}/api/book/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}

    async getBooksByAuthorId(id) {
        return await fetch(`${this.baseUrl}/api/book/author/${id}`, {
            method: "GET",
        }).then(() => {
            console.log(id)
        })
    }
}
