import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {AddAuthor} from "./AddAuthor";
import {AuthorItem} from "./AuthorItem";

export function Author() {

    const [authors, setAuthors] = useState([]);
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [ran, setRan] = useState(false);

    useEffect(() => {
        let authorService = new AuthorApi()
        if (ran === false) {
            authorService.getAuthors()
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setAuthors(result);
                        setRan(true)
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }

    }, [])

    return (
        <div className="container">
            <div className="content contact-list">
                <div className="card card-default">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddAuthor></AddAuthor>
                    </div>
                    <div className="card-body px-3 px-md-5">
                        <div className="row">
                            {authors.map(author => <AuthorItem author={author} key={author.id}
                                                               className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                            </AuthorItem>)}
                        </div>
                    </div>
                </div>
            </div>
        </div>

    )
}
