import useAxiosPrivate from "./useAxiosPrivate";

export class BookApi {
    protectedAxios = new useAxiosPrivate();
    controller = new AbortController();

    async getBooks(){
        await this.protectedAxios.get('/api/book/getAll', {
            signal: this.controller.signal
        });
    }
    async addBookByAdmin(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return this.protectedAxios
            .post(`/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async deleteBook(id){
        return this.protectedAxios.delete(`/api/book/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}

    async addBookByAuthor(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return this.protectedAxios
            .post(`${this.baseUrl}/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async getBooksByAuthorId(id){
        return this.protectedAxios.get(`/api/book/author/${id}`)
    }

}
