import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal} from "react-bootstrap";
import {BookApi} from "../../api/BookApi";
import {Author} from "../author/Author";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faBook} from '@fortawesome/fontawesome-free-solid'
import {Guid} from "js-guid";

export const AddBook = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [error, setError] = useState(null);
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [photo, setPhoto] = useState("");
    const [authorId, setAuthorId] = useState("");
    const [categoriesIds, setCategoriesIds] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [authors, setAuthors] = useState([]);

    let bookService = new BookApi()

    useEffect(() => {
        let authorService = new AuthorApi()
        authorService.getAuthors()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setAuthors(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    const handleSubmit = event => {
        event.preventDefault();
        debugger
        const book = {name, description, photo, authorId, categoriesIds};
        bookService.addBook(book);
        // eslint-disable-next-line no-restricted-globals
        // location.reload()
    }

    return (
        <>
            <Button variant="warning" onClick={handleShow}>
                <FontAwesomeIcon icon="fa-book"></FontAwesomeIcon> Add Book
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add Book</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit}>
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={description}
                               placeholder="Description"
                               onChange={(e) => setDescription(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={photo}
                               placeholder="photo"
                               onChange={(e) => setPhoto(e.target.value)}
                        ></input>
                        <p/>
                        {/*<input className="form-control"*/}
                        {/*type="text"*/}
                        {/*value={authorId}*/}
                        {/*placeholder="thii"*/}
                        {/*onChange={(e) => setAuthorId(e.target.value)}>*/}
                        {/*</input>*/}
                        <input>
                            <select className="form-select"
                                    value={authorId}
                                    type= "text"
                                    onChange={(e) => setAuthorId(e.target.value)}>
                                {authors.map(author => <option  key={author.id}>
                                    {author.name}
                                </option>)}
                            </select>
                        </input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={categoriesIds}
                               placeholder="catId"
                               onChange={(e) => setCategoriesIds(e.target.value)}
                        ></input>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
