        formData.append('CategoriesIds', book[1].value)
        formData.append('Name', book[2].value)
        formData.append('Description', book[3].value)
        formData.append('PhotoFile', book[4].value)

        return axios
            .post(`${this.baseUrl}/api/book/add`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
