import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {Author} from "./Author";

export function AuthorItem() {

    const [authors, setAuthors] = useState([]);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <div className="card-body px-3 px-md-5">
            <div className="row">
                <div className="card card-default p-4">
                    <Author>
                        <div className="media-body">
                            <h5 className="mt-0 mb-2 text-dark">{author.name}</h5>
                            <li className="d-flex">
                                <i className="mdi mdi-map mr-1"></i>
                                <span>{author.bio}</span>
                            </li>
                            <Modal
                                show={show}
                                onHide={handleClose}
                                backdrop="static"
                                keyboard={false}
                            >
                                <Modal.Header closeButton>
                                    <Modal.Title>Are you sure you want to delete {author.name}?</Modal.Title>
                                </Modal.Header>
                                <Modal.Body>
                                    <Modal.Footer>
                                        <Button variant="secondary" onClick={handleClose}>
                                            Close
                                        </Button>
                                        <Button variant={"danger"} onClick={() => deleteAuthor(author.id)}>
                                            Delete
                                        </Button>
                                    </Modal.Footer>
                                </Modal.Body>
                            </Modal>
                            <p/>
                            <Button onClick={handleShow} className={"bg-danger"}>
                                <FontAwesomeIcon icon="trash"> </FontAwesomeIcon>

                            </Button>

                        </div>
                    </Author>
                </div>
                <p/>
            </div>
            )}
        </div>
        // </div>
    )
}
