import {useEffect, useRef, useState} from "react";
import {UserApi} from "../../api/UserApi";
import {AddUser} from "../user/AddUser";
import {AuthorItem} from "./AuthorItem";
import {AuthorBooks} from "./AuthorBooks";
import {BookApi} from "../../api/BookApi";

export function Author() {

    const [authors, setAuthors] = useState([]);
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const effectRan = useRef(false)

    useEffect(() => {
        let authorService = new UserApi()
        let bookService = new BookApi()
        if (effectRan.current === false) {
            effectRan.current = true
            authorService.getAuthors()
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setAuthors(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }

    }, [])

    return (

        <div className="container">
            <div className="card-body px-3 px-md-5">
                <div className="row">
                    {authors.map(author => <AuthorItem author={author} key={author.id}
                                                       className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                    </AuthorItem>)}
                </div>
            </div>
            <div className="card-body px-3 px-md-5">
                <div className="row">
                    {books.map(book => <AuthorBooks book={book} key={book.id}
                                                    className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                    </AuthorBooks>)}
                </div>
            </div>
        </div>
    )
}
