import {useEffect, useRef, useState} from "react";
import {CategoryApi} from "../../api/CategoryApi";
import {AddAuthor} from "../user/AddAuthor";
import {AuthorItem} from "../author/AuthorItem";
import {CategoryItem} from "./CategoryItem";
import {AddCategory} from "./AddCategory";

export function Category() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [categories, setCategories] = useState([]);
    const effectRan = useRef(false)

    useEffect(() => {
        let categoryService = new CategoryApi()
        if(effectRan.current === false){
            effectRan.current = true
            categoryService.getCategories()
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setCategories(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }

    }, [])

    return (
        <div className="container ">
            <div className="content contact-list ">
                <div className="card card-default ">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddCategory/>
                    </div>
                    <table className="table table-sm">
                        <thead className="table">
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Priority</th>
                            <th scope="col">Options</th>
                        </tr>
                        </thead>
                        <tbody>
                        {categories.map(category => <CategoryItem category={category} key={category.id}
                                                                  className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                        </CategoryItem>)}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
