import {useEffect, useState} from "react";
import {UserApi} from "../../api/UserApi";
import {Button, Modal} from "react-bootstrap";
import {BookApi} from "../../api/BookApi";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {CategoryApi} from "../../api/CategoryApi";

export const AddBook = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [authors, setAuthors] = useState([]);
    const [categories, setCategories] = useState([]);

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [photo, setPhoto] = useState('');

    const [authorId, setAuthorId] = useState("");
    const [categoriesIds, setCategoriesIds] = useState([]);


    let bookService = new BookApi()

    useEffect(() => {
        let authorService = new UserApi()
        let categoryService = new CategoryApi()
        authorService.getAuthors()
        // categoryService.getCategories()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setAuthors(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    const handleSubmit = event => {
        debugger
        event.preventDefault();
        const book = {name, description, photo, authorId, categoriesIds};

        bookService.addBook(book)
        // eslint-disable-next-line no-restricted-globals
        // location.reload()
        // const route = 'https://localhost:7196/api/book/add';
        // const payload = {
        //     caption: "This is image",
        //     imageBase: photo,
        // };
        // axios.post(route, book, {
        //     maxBodyLength: 20000000,
        // });
    }


    return (
        <>
            <Button variant="warning" onClick={handleShow}>
                <FontAwesomeIcon icon="fa-book"></FontAwesomeIcon> Add Book
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add Book</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className={"form"} onSubmit={handleSubmit}>
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={description}
                               placeholder="Description"
                               onChange={(e) => setDescription(e.target.value)}
                        ></input>
                        <p/>
                        Book Cover:
                        <input className="form-control-file"
                               type="file"
                               value={''}
                               placeholder="photo"
                               onChange={(e) => setPhoto(e.target.files[0])}
                        ></input>
                        <p/>
                        {/*<input className="form-control"*/}
                        {/*       type="text"*/}
                        {/*       value={authorId}*/}
                        {/*       placeholder="Description"*/}
                        {/*       onChange={(e) => setAuthorId(e.target.value)}*/}
                        {/*></input>*/}
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={categoriesIds}
                               placeholder="cat"
                               onChange={(e) => setCategoriesIds(e.target.value)}
                        ></input>
                        <select className="form-select"
                                onChange={(e) => setAuthorId(e.target.value)}
                                value={'item'}>
                                {authors.map(author => <option value={author.id} key={author.id}>
                                {author.name}
                            </option>)}
                        </select>
                        <p/>
                        {/*<div className="btn-group"*/}
                        {/*        typeof = "checkbox"*/}
                        {/*        >*/}
                        {/*    <label className="btn-primary">*/}
                        {/*        {categories.map(category => <input key={category.id} value={categoriesIds}*/}
                        {/*                                           onChange={(e) => setCategoriesIds(e.target.value)}>*/}
                        {/*            {category.name}*/}
                        {/*        </input>)}*/}
                        {/*    </label>*/}
                        {/*</div>*/}
                        {/*<select className="btn-group"*/}
                        {/*       type="checkbox"*/}
                        {/*       value={categoriesIds}*/}
                        {/*       placeholder="catId"*/}
                        {/*       onChange={(e) => setCategoriesIds(e.target.value)}*/}
                        {/*       {authors.map(author => <option key={author.id} id={author.id}>*/}
                        {/*           {author.name}*/}
                        {/*       </option>)}*/}
                        {/*></select>*/}
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
