import {Component, useState} from "react";
import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {UserApi} from "../../api/UserApi";
import {faUserPlus} from '@fortawesome/fontawesome-free-solid'
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {useEffect, useRef} from "react";
import useAxiosPrivate from "../../api/useAxiosPrivate";

export const AddUser = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [name, setName] = useState("");
    const [bio, setBio] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [roleId, setRoleId] = useState("");
    const [email, setEmail] = useState("");

    const effectRan = useRef(false)
    const [roles, setRoles] = useState([]);

    const protectedAxios = useAxiosPrivate();
    const controller = new AbortController();

    const handleSubmit = event => {
        event.preventDefault();
        const author = {name, bio, username, email, password, roleId};
        debugger
        authorService.addUser(author);
        // eslint-disable-next-line no-restricted-globals
        // location.reload()
    }

    useEffect(() => {
        let authorService = new UserApi()
        if (effectRan.current === false) {
            effectRan.current = true
            authorService.getRoles()
                .then(function
                    (response) {
                    console.log(response);
                    setRoles(response.data)
                }).catch(function (error) {
                console.log(error);
            });
        }

    }, [])

    return (
        <>
            <Button variant="success" onClick={handleShow}>
                <FontAwesomeIcon icon="fa-user-plus"></FontAwesomeIcon> Add User
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add User</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit}>
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={bio}
                               placeholder="Bio"
                               onChange={(e) => setBio(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={username}
                               placeholder="Username"
                               onChange={(e) => setUsername(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={email}
                               placeholder="Email"
                               onChange={(e) => setEmail(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={password}
                               placeholder="Password"
                               onChange={(e) => setPassword(e.target.value)}
                        ></input>
                        <p/>
                        <select className="form-select"
                                onChange={(e) => setRoleId(e.target.value)}
                                value={roleId}>
                            {roles.map(role => <option key={role.id} value={role.id} >
                                {role.name}
                            </option>)}
                        </select>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
