import {useEffect, useState, useRef} from "react";
import {BookApi} from "../../api/BookApi";
import {AddBook} from "./AddBook";

import {BookItem} from "./BookItem";
import {AuthorBooks} from "../author/AuthorBooks";
import {useParams} from "react-router-dom";

export function Book() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [books, setBooks] = useState([]);
    const [authorBooks, setAuthorBooks] = useState([]);
    const effectRan = useRef(false)

    const {id} = useParams()

    useEffect(() => {
        let bookService = new BookApi()
        if (effectRan.current === false) {
            effectRan.current = true
            bookService.getBooks()
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setBooks(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
            bookService.getBooksByAuthorId(id)
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setAuthorBooks(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }
    }, [])


    return (
        <div className="container">
            <div className="content contact-list">
                <div className="card card-default">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddBook></AddBook>
                    </div>
                    <div className="card-body px-3 px-md-5">
                        <div className="row">
                            {books.map(book => <BookItem book={book} key={book.id}
                                                         className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                            </BookItem>)}
                        </div>
                    </div>
                </div>
                <div className="card-body px-3 px-md-5">
                    <div className="row">
                        {authorBooks.map(book => <AuthorBooks book={book} key={book.id}
                                                              className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                        </AuthorBooks>)}
                    </div>
                </div>
            </div>
        </div>
    )
}
