import {useEffect, useRef, useState} from "@types/react";
import {UserApi} from "../../api/UserApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {AddCategory} from "../category/AddCategory";
import {CategoryItem} from "../category/CategoryItem";
import {UserItem} from "./UserItem";
import {AddUser} from "./AddUser";

export function User(){

    const [users, setUsers] = useState([]);
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const effectRan = useRef(false)

    useEffect(() => {
        let authorService = new UserApi()
        if (effectRan.current === false) {
            effectRan.current = true
            authorService.getAuthors()
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log("Response", result);
                        setIsLoaded(true);
                        setUsers(result);
                    },
                    (error) => {
                        console.log("Error", error);
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }

    }, [])

    return (
        <div className="container ">
            <div className="content contact-list ">
                <div className="card card-default ">
                    <div className="card-header align-items-center px-3 px-md-5">
                        <AddUser/>
                    </div>
                    <table className="table table-sm">
                        <thead className="table">
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Username</th>
                            <th scope="col">Role</th>
                            <th scope="col">Options</th>
                        </tr>
                        </thead>
                        <tbody>
                        {users.map(user => <UserItem user={user} key={user.id}
                                                                  className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                        </UserItem>)}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
