import {useEffect, useState} from "react";
import {BookApi} from "../../api/BookApi";
import {AuthorApi} from "../../api/AuthorApi";

export const BookItem = ({book}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function deleteBook(id) {
        let bookService = new BookApi()
        bookService.deleteBook(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    return (
        <div className="card card-default p-4">
            <div className="media-body">
                <h5 className="mt-0 mb-2 text-dark">{book.name}</h5>
                <ul className="list-unstyled text-smoke text-smoke">
                    <li className="d-flex">
                        <i className="mdi mdi-map mr-1"></i>
                        <span>{book.description}</span>
                    </li>
                    <li className="d-flex">
                        <i className="mdi mdi-map mr-1"></i>
                        <span>{book.photo}</span>
                    </li>
                    {/*<li className="d-flex">*/}
                    {/*    <i className="mdi mdi-map mr-1"></i>*/}
                    {/*    <span>{book.author}</span>*/}
                    {/*</li>*/}
                </ul>
            </div>
        </div>
    )
}
