    const logout = () => {
        try {
            debugger
            localStorage.removeItem("token")
            setAuth({})

        //     const accessToken = "";
        //     const role = "";
        //     const id = "";
        //     await setAuth({accessToken, role, id});
        } catch (err) {
            console.log(err)
        }
        return logout;
    }
}
export default useLogout
