import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

export const AuthorItem = ({author}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [authors, setAuthors] = useState([]);

    function deleteAuthor(id) {
        let authorService = new AuthorApi()
        authorService.deleteAuthor(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    return (
        <div className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
            <div className="card card-default p-4">
                <h5 className="mt-0 mb-2 text-dark">{author.name}</h5>
                <li className="d-flex">
                    <i className="mdi mdi-map mr-1"></i>
                    <span>{author.bio}</span>
                </li>
                <p/>
                {/*<a onClick={handleShow} className="bg-danger" data-placement="top"*/}
                {/*data-original-title="Delete">*/}
                <span className="bg-danger" typeof="button" onClick={handleShow} >
              <i className="fa fa-square fa-stack"></i>
              <i className="fa fa-trash-can fa-stack-2x fa-inverse"></i>
            </span>
            {/*</a>*/}
                <Modal
                    show={show}
                    onHide={handleClose}
                    backdrop="static"
                    keyboard={false}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Are you sure you want to delete {author.name}?</Modal.Title>
                    </Modal.Header>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant={"danger"} onClick={() => deleteAuthor(author.id)}>
                            Delete
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        </div>
    )
}
