import {useEffect, useState} from "react";
import {CategoryApi} from "../../api/CategoryApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import button from "bootstrap/js/src/button";
import {faTrash} from '@fortawesome/fontawesome-free-solid'

export function CategoryItem(){
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [categories, setCategories] = useState([]);

    function deleteCategory(id) {
        let categoryService = new CategoryApi()
        categoryService.deleteCategory(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    useEffect(() => {
        let categoryService = new CategoryApi()
        categoryService.getCategories()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setCategories(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return(
        <div className="main-box-body clearfix">
            <div className="table-responsive">
                <table className="table user-list">
                    <thead className="sort" data-sort="productName">
                    <tr>
                        <th className="align-left">Name</th>
                        <th className="text-center">Priority</th>
                        <th className="text-center">Options</th>
                        <th>&nbsp;</th>
                    </tr>
                    </thead>
                    <tbody >
                    {categories.map(category => <div key={category.id}>
                    <tr>
                        <td className="align-left">
                            <a className="user-list">{category.name} </a>
                        </td>
                        <td className="text-center">
                            <a> {category.priority}</a>
                        </td>
                        <td className="text-center">
                            <a onClick={handleShow}
                            data-placement="top" data-original-title="Edit">
                            <span className="fa-stack" typeof={button}>
                      <FontAwesomeIcon icon="trash"> </FontAwesomeIcon>
                    </span></a>
                        </td>
                        <Modal
                            show={show}
                            onHide={handleClose}
                            backdrop="static"
                            keyboard={false}
                        >
                            <Modal.Header closeButton>
                                <Modal.Title>Are you sure you want to delete {category.name}?</Modal.Title>
                            </Modal.Header>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={handleClose}>
                                    Close
                                </Button>
                                <Button variant={"danger"} onClick={() => deleteCategory(category.id)}>
                                    Delete
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </tr>
                    </div>)}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
