import {useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal} from "react-bootstrap";
import {BookApi} from "../../api/BookApi";
import {Author} from "../author/Author";

export const AddBook = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [photo, setPhoto] = useState("");
    const [authorId, setAuthorId] = useState("");
    const [categoriesIds, setCategoriesIds] = useState("");

    const [authors, setAuthors] = useState([]);

    let bookService = new BookApi()

    function getAuthor() {
        let authorService = new AuthorApi()
        authorService.getAuthors()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setAuthors(result);
                })
    }

    const handleSubmit = event => {
        event.preventDefault();
        const book = {name, description, photo, authorId, categoriesIds};
        bookService.addBook(book);
        // eslint-disable-next-line no-restricted-globals
        // location.reload()
    }

    return (
        <>
            <Button variant="warning" onClick={handleShow}>
                Add Book
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add Book</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit}>
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={description}
                               placeholder="Description"
                               onChange={(e) => setDescription(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={photo}
                               placeholder="photo"
                               onChange={(e) => setPhoto(e.target.value)}
                        ></input>
                        <p/>
                        <select className="form-select"
                                value={authorId}
                                onChange={(e) => setAuthorId(e.target.value)}>
                            {authors.map(author => <option key={author.id}
                                                           className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                            </option>)}
                        </select>

                        {/*<div className="dropdown">*/}
                        {/*<button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton"*/}
                        {/*        data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"*/}

                        {/*        value={authorId}*/}
                        {/*        onChange={(e) => setAuthorId(e.target.value)}>*/}
                        {/*    Author*/}
                        {/*</button>*/}
                        {/*    {authors.map(author => <div key={author.id}*/}
                        {/*                                className="col-lg-6 col-xl-4 col-md-6 col-sm-12">*/}
                        {/*<div className="dropdown-menu" aria-labelledby="dropdownMenuButton">*/}
                        {/*    <a className="dropdown-item" href="#">{author.name}</a>*/}
                        {/*</div>*/}
                        {/*</div>)}*/}
                        {/*</div>*/}
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={categoriesIds}
                               placeholder="catId"
                               onChange={(e) => setCategoriesIds(e.target.value)}
                        ></input>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
