import {useEffect, useState} from "react";
import {CategoryApi} from "../../api/CategoryApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import button from "bootstrap/js/src/button";
import {faTrash} from '@fortawesome/fontawesome-free-solid'

export const CategoryItem = ({category}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function deleteCategory(id) {
        let categoryService = new CategoryApi()
        debugger
        categoryService.deleteCategory(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    return (
        <div>
            <tr>
                <td>{category.name}</td>
                <td>{category.priority}</td>
                <td>
                    <Button onClick={handleShow} className="btn btn-danger">
                        <FontAwesomeIcon icon="trash"> </FontAwesomeIcon>
                    </Button>
                </td>
                <Modal
                    show={show}
                    onHide={handleClose}
                    backdrop="static"
                    keyboard={false}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Are you sure you want to delete {category.name}?</Modal.Title>
                    </Modal.Header>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant={"danger"} onClick={() => deleteCategory(category.id)}>
                            Delete
                        </Button>
                    </Modal.Footer>
                </Modal>
            </tr>
        </div>
    )
}
