import {Component, useState} from "react";
import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {AuthorApi} from "../../api/AuthorApi";

export const AddAuthor = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [name, setName] = useState("");
    const [bio, setBio] = useState("");

    let authorService = new AuthorApi()

    const handleSubmit = event => {
        event.preventDefault();
        const author = {name, bio};
        fetch(`https://localhost:7196/api/author/add/`, {
            method: "POST",
            // headers:{"Content-Type" : "application/json"},
            body: JSON.stringify(author)
        }).then(() =>{
            console.log("added")
        })
    }

    // let handleSubmit = async (e) => {
    //     e.preventDefault();
    //     try {
    //         let res = await fetch("https://localhost:7196/api/author/add", {
    //             method: "POST",
    //             body: JSON.stringify(au),
    //         });
    //         let resJson = await res.json();
    //         if (res.status === 200) {
    //             setName("");
    //             setBio("");
    //             setMessage("User created successfully");
    //         } else {
    //             setMessage("Some error occured");
    //         }
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };
    return (
        <>
        <Button variant="primary" onClick={handleShow}>
            Add Author
        </Button>
        <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={false}
        >
            <Modal.Header closeButton>
                <Modal.Title>Add Author</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={name}
                        placeholder="Name"
                        onChange={(e) => setName(e.target.value)}
                    />
                    <input
                        type="text"
                        value={bio}
                        placeholder="Bio"
                        onChange={(e) => setBio(e.target.value)}
                    />
                    <button>Create</button>
                </form>
        </Modal.Body>
    <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
            Close
        </Button>
        <Button variant="primary" type="submit">Add</Button>
    </Modal.Footer>
</Modal>
</>
)
    ;
}
