import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

export function AuthorItem(){

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [authors, setAuthors] = useState([]);

    function deleteAuthor(id) {
        let authorService = new AuthorApi()
        authorService.deleteAuthor(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    useEffect(() => {
        let authorService = new AuthorApi()
        authorService.getAuthors()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setAuthors(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return(
        <div className="card-body px-3 px-md-5">
            <div className="row">

                    <div className="card card-default p-4">
                        {authors.map(author => <div key={author.id}
                                                    className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
                        <div className="media-body">
                            <h5 className="mt-0 mb-2 text-dark">{author.name}</h5>
                            <li className="d-flex">
                                <i className="mdi mdi-map mr-1"></i>
                                <span>{author.bio}</span>
                            </li>
                            <Modal
                                show={show}
                                onHide={handleClose}
                                backdrop="static"
                                keyboard={false}
                            >
                                <Modal.Header closeButton>
                                    <Modal.Title>Are you sure you want to delete {author.name}?</Modal.Title>
                                </Modal.Header>
                                <Modal.Body>
                                    <Modal.Footer>
                                        <Button variant="secondary" onClick={handleClose}>
                                            Close
                                        </Button>
                                        <Button variant={"danger"} onClick={()=> deleteAuthor(author.id)}>
                                            Delete
                                        </Button>
                                    </Modal.Footer>
                                </Modal.Body>
                            </Modal>
                            <p/>
                            <Button onClick={handleShow} className={"bg-danger"}>
                                <FontAwesomeIcon icon="trash" > </FontAwesomeIcon>
                            </Button>
                        </div>
                    </div>)}
                    <p/>
                </div>
                </div>
            </div>
        // </div>
    )
}
