import {useEffect, useState} from "react";
import {AuthorApi} from "../../api/AuthorApi";
import {Button, Modal, Nav} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/fontawesome-free-solid'
import {generatePath, NavLink, useHistory} from "react-router-dom";

export const AuthorItem = ({author}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

const id = author.id
    const history = useHistory();

    function deleteAuthor(id) {
        let authorService = new AuthorApi()
        debugger
        authorService.deleteAuthor(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    const handleProceed = (e) => {
        id && history.push(generatePath("/author/:id", { id }));
    };

    return (
        <div className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
            <div className="card card-default p-4">
                <h5 className="mt-0 mb-2 text-dark">{author.name}</h5>
                <li className="d-flex">
                    <span>{author.bio}</span>
                </li>
                <p/>
                <div>
                    <Button onClick={handleShow} className="btn btn-danger">
                        <FontAwesomeIcon icon="trash"> </FontAwesomeIcon>
                    </Button>
                </div>
                <p/>
                <div>
                    <Nav.Link  as={NavLink}>
                        <Button variant="info"> Books</Button>
                    </Nav.Link>
                </div>
                <Modal
                    show={show}
                    onHide={handleClose}
                    backdrop="static"
                    keyboard={false}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Are you sure you want to delete {author.name}?</Modal.Title>
                    </Modal.Header>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant={"danger"} onClick={() => deleteAuthor(author.id)}>
                            Delete
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        </div>
    )
}
