import {useEffect, useState} from "react";
import {BookApi} from "../../api/BookApi";

export const BookItem = ({book}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function deleteBook(id) {
        let bookService = new BookApi()
        bookService.deleteBook(id);
        // eslint-disable-next-line no-restricted-globals
        location.reload()
    }

    return (
        <div className="col-lg-6 col-xl-4 col-md-6 col-sm-12">
            <div className="card card-default p-4">
                <h5 className="mt-0 mb-2 text-dark">{book.name}
                    {book.photoFile}</h5>
                    <li className="d-flex">
                        <i className="mdi mdi-map mr-1"></i>
                        <span>{book.description}</span>
                    </li>
                <li className="d-flex">
                    <i className="mdi mdi-map mr-1"></i>
                    <span>{book.authorName}</span>
                </li>
                    {/*<li className="d-flex">*/}
                    {/*    <i className="mdi mdi-map mr-1"></i>*/}
                    {/*    <span>{book.photoName}</span>*/}
                    {/*</li>*/}
                    {/*<li className="d-flex">*/}
                    {/*    <i className="mdi mdi-map mr-1"></i>*/}
                    {/*    <span>{book.author}</span>*/}
                    {/*</li>*/}
            </div>
        </div>
    )
}
