import {useEffect, useState} from "react";
import {UserApi} from "../../api/UserApi";
import {Button, Modal} from "react-bootstrap";
import {BookApi} from "../../api/BookApi";
import {Author} from "../author/Author";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faBook} from '@fortawesome/fontawesome-free-solid'
import {Guid} from "js-guid";
import axios from "axios";

export const AddBook = () => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [authors, setAuthors] = useState([]);

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [photo, setPhoto] = useState("");
    const [imageSrc, setImageSrc] = useState("");
    const [authorId, setAuthorId] = useState("");
    const [categoriesIds, setCategoriesIds] = useState([]);


    let bookService = new BookApi()

    useEffect(() => {
        let authorService = new UserApi()
        authorService.getAuthors()
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("Response", result);
                    setIsLoaded(true);
                    setAuthors(result);
                },
                (error) => {
                    console.log("Error", error);
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    const handleSubmit = event => {
        event.preventDefault();
        const book = {name, description, photo, authorId, categoriesIds};
        bookService.addBook(book)
        // bookAPI().create(book);
        // eslint-disable-next-line no-restricted-globals
        // location.reload()
    }

    // const bookAPI = (url = 'https://localhost:7196/api/book/add') => {
    //     return {
    //         create: newRecord => axios.post(url, newRecord),
    //     }
    // }

    return (
        <>
            <Button variant="warning" onClick={handleShow}>
                <FontAwesomeIcon icon="fa-book"></FontAwesomeIcon> Add Book
            </Button>
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Add Book</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit} encType="multipart/form-data">
                        <input className="form-control"
                               type="text"
                               value={name}
                               placeholder="Name"
                               onChange={(e) => setName(e.target.value)}
                        ></input>
                        <p/>
                        <input className="form-control"
                               type="text"
                               value={description}
                               placeholder="Description"
                               onChange={(e) => setDescription(e.target.value)}
                        ></input>
                        <p/>
                        Book Cover:
                        <input className="form-control-file"
                               type="file"
                               accept="image/*"
                               value={photo}
                               placeholder="photo"
                               onChange={(e) => setPhoto(e.target.value)}
                        ></input>
                        <p/>
                        {/*<input className="form-control"*/}
                        {/*type="text"*/}
                        {/*value={authorId}*/}
                        {/*placeholder="thii"*/}
                        {/*onChange={(e) => setAuthorId(e.target.value)}>*/}
                        {/*</input>*/}
                        <select className="form-select"
                                value={authorId}
                                onChange={(e) => setAuthorId(e.target.value)}>
                            {authors.map(author => <option key={author.id} id={author.id}>
                                {author.name}
                            </option>)}
                        </select>
                        <p/>
                        <input className="form-control"
                               value={categoriesIds}
                               placeholder="catId"
                               onChange={(e) => setCategoriesIds(e.target.value)}
                        ></input>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" type="submit">Add</Button>
                        </Modal.Footer>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
