import axios from "./axios";

export class BookApi{

    baseUrl = "https://localhost:7196"

    async getBooks(){
        return axios.get(`/api/book/getAll`, {
            headers:{
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            }
        })


    }
    async addBookByAdmin(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return axios
            .post(`/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
                console.log(res);
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async deleteBook(id){
        return axios.delete(`/api/book/delete/${id}`, {
            method: "DELETE",
            headers:{"Accept" : "application/json",
                "Content-Type" : "application/json"},
        }).then(() =>{
            console.log("deleted")
        })}

    async addBookByAuthor(book) {
        const formData = new FormData();
        formData.append('AuthorId', book.authorId)
        for (let i=0; i< book.categoriesIds.length; i++) {
            formData.append('CategoriesIds', book.categoriesIds[i]);
        }
        formData.append('Name', book.name)
        formData.append('Description', book.description)
        formData.append('PhotoFile', book.photo)
        return axios
            .post(`${this.baseUrl}/api/book/addByAdmin`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            })
            .then((res) => {
                return res;
            })
            .catch((err) => {
                console.log(err);
            });
    }

    async getBooksByAuthorId(id){
        return axios.get(`/api/book/author/${id}`)
    }

}
